CREATE DATABASE IF NOT EXISTS mglsi_news;
USE mglsi_news;

DROP TABLE IF EXISTS Article, Categorie;

CREATE TABLE Article (
    id INT PRIMARY KEY AUTO_INCREMENT,
    titre VARCHAR(255),
    contenu TEXT,
    dateCreation DATETIME DEFAULT CURRENT_TIMESTAMP,
    dateModification DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    categorie INT
);

CREATE TABLE Categorie (
    id INT PRIMARY KEY AUTO_INCREMENT,
    libelle VARCHAR(20)
);

INSERT INTO Categorie (libelle) VALUES ('Sport'), ('Santé'), ('Éducation'), ('Politique');

